CREATE TABLE `MSUUS`.`targets` ( `id` INT NOT NULL AUTO_INCREMENT , `type` VARCHAR(32) NOT NULL , `latitude` VARCHAR(64) NOT NULL , `longitude` VARCHAR(64) NOT NULL , `orientation` VARCHAR(32) NOT NULL , `shape` VARCHAR(32) NOT NULL , `background_color` VARCHAR(32) NOT NULL , `alphanumeric` VARCHAR(32) NOT NULL , `alphanumeric_color` VARCHAR(32) NOT NULL , `confidence` INT(3) NULL , `image_path` VARCHAR(128) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
